content:
#gameBoard {
    display: grid;
    grid-template-columns: repeat(10, 20px);
    grid-template-rows: repeat(10, 20px);
}

.cell {
    width: 20px;
    height: 20px;
    border: 1px solid black;
    background-color: white;
}

.alive {
    background-color: black;
}

const boardSize = 10;
let gameBoard = [];
let interval;

document.getElementById('startButton').addEventListener('click', startGame);

function initializeBoard() {
    const boardElement = document.getElementById('gameBoard');
    for (let i = 0; i < boardSize; i++) {
        gameBoard[i] = [];
        for (let j = 0; j < boardSize; j++) {
            gameBoard[i][j] = false;
            let cell = document.createElement('div');
            cell.className = 'cell';
            cell.addEventListener('click', () => toggleCell(i, j));
            boardElement.appendChild(cell);
        }
    }
}

function toggleCell(x, y) {
    gameBoard[x][y] = !gameBoard[x][y];
    updateBoard();
}

function updateBoard() {
    const cells = document.getElementsByClassName('cell');
    for (let i = 0; i < boardSize; i++) {
        for (let j = 0; j < boardSize; j++) {
            let cell = cells[i * boardSize + j];
            cell.className = gameBoard[i][j] ? 'cell alive' : 'cell';
        }
    }
}

function startGame() {
    if (interval) {
        clearInterval(interval);
    }
    interval = setInterval(nextGeneration, 500);
}

function nextGeneration() {
    let newBoard = JSON.parse(JSON.stringify(gameBoard));
    for (let x = 0; x < boardSize; x++) {
        for (let y = 0; y < boardSize; y++) {
            let neighbors = countNeighbors(x, y);
            if (gameBoard[x][y]) {
                if (neighbors < 2 || neighbors > 3) {
                    newBoard[x][y] = false;
                }
            } else {
                if (neighbors === 3) {
                    newBoard[x][y] = true;
                }
            }
        }
    }
    gameBoard = newBoard;
    updateBoard();
}

function countNeighbors(x, y) {
    let count = 0;
    for (let i = -1; i <= 1; i++) {
        for (let j = -1; j <= 1; j++) {
            if (i === 0 && j === 0) continue;
            let newX = x + i;
            let newY = y + j;
            if (newX >= 0 && newX < boardSize && newY >= 0 && newY < boardSize && gameBoard[newX][newY]) {
                count++;
            }
        }
    }
    return count;
}

window.onload = initializeBoard;
